var userInput = prompt("Please enter the weather condition (sunny, rainy, cloudy, snowy, windy):").toLowerCase();

var weatherConditions = {
sunny: "data:image/jpeg;base64,", // Replace with actual image URL
rainy: "data:image/jpeg;base64,", // Replace with actual image URL
cloudy: "data:image/jpeg;base64,", // Replace with actual image URL
snowy: "data:image/jpeg;base64,", // Replace with actual image URL
windy: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQJIWgRe2OqkVA2gpo23tRz3M4gbIObwqEGfA&usqp=CAU"  // Replace with actual image URL
            };
if (userInput && weatherConditions[userInput]) {
var weatherImage = weatherConditions[userInput];
var weatherReport = `
    <div class="card weather-card">
        <div class="card-body">
        <h5 class="card-title">Weather Report</h5>
        <img src="${weatherImage}" class="rounded-circle" alt="Weather Image">
        <p class="card-text">Weather: ${userInput.charAt(0).toUpperCase() + userInput.slice(1)}</p>
    </div>
</div>
                `;
                
document.write(weatherReport);} else {
document.write("<p><img src='data:image/jpeg;base64,'>Weather condition not applied.</img></p>");
            }